import { Component, OnInit } from '@angular/core';
import { FormBuilder,FormGroup } from '@angular/forms';
import { patientModel } from './patient.model';
import { ApiService } from '../shared/api.service';
@Component({
  selector: 'app-patient',
  templateUrl: './patient.component.html',
  styleUrls: ['./patient.component.css']
})
export class PatientComponent implements OnInit{
  formValue !: FormGroup;
  patientModelobj : patientModel = new patientModel();
  patientData !: any;
  showAdd !:boolean;
  showUpdate !:boolean;
  constructor(private formbuilder: FormBuilder,
    private api:ApiService) {
  }

  ngOnInit(): void {
    this.formValue = this.formbuilder.group({
      firstName :[''],
      lastName :[''],
      age:[''],
      gender:[''],
      dob:[''],
      phone:['']

    })
    this.getAll();
  }
  addBtn(){
    this.formValue.reset();
    this.showAdd = true;
    this.showUpdate = false;
  }


  postPatient(){
    this.patientModelobj.firstName = this.formValue.value.firstName;
    this.patientModelobj.lastName = this.formValue.value.lastName;
    this.patientModelobj.age = this.formValue.value.age;
    this.patientModelobj.gender = this.formValue.value.gender;
    this.patientModelobj.dob = this.formValue.value.dob;
    this.patientModelobj.phone = this.formValue.value.phone;

    this.api.postPatient(this.patientModelobj).subscribe(res=>{
      console.log(res);
      alert("Patient added successfully")
      this.formValue.reset();
      this.getAll();
      let ref = document.getElementById('cancle');
      ref?.click();
    },
    err => {
      alert("Something went wrong")
    })
  }
  getAll(){
    this.api.getPatient().subscribe(res=>{
      this.patientData = res;
      })
  }
  delete(n:any){
    this.api.deletePatient(n.id).subscribe(res=>{
      alert("Deleted")
      this.getAll();
    })
  }
  update(n:any){
    this.showUpdate = true;
    this.showAdd = false;
    this.patientModelobj.id = n.id;
    this.formValue.controls['firstName'].setValue(n.firstName);
    this.formValue.controls['lastName'].setValue(n.lastName);
    this.formValue.controls['age'].setValue(n.age);
    this.formValue.controls['gender'].setValue(n.gender);
    this.formValue.controls['dob'].setValue(n.dob);
    this.formValue.controls['phone'].setValue(n.phone);
  }
  updateBtn(){
    this.patientModelobj.firstName = this.formValue.value.firstName;
    this.patientModelobj.lastName = this.formValue.value.lastName;
    this.patientModelobj.age = this.formValue.value.age;
    this.patientModelobj.gender = this.formValue.value.gender;
    this.patientModelobj.dob = this.formValue.value.dob;
    this.patientModelobj.phone = this.formValue.value.phone;
    this.api.updatePatient(this.patientModelobj,this.patientModelobj.id).subscribe(res=>{
      alert("updated");
      let ref = document.getElementById('cancle');
      ref?.click();
      this.getAll();
    })
  }



}


<nav class="navbar navbar-expand-lg bg-primary">
  <div class="container-fluid">



    <h4>

      <a style="color: aliceblue;" class="navbar-brand" href="#">Navbar</a>
    </h4>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>



    <div class="collapse navbar-collapse" id="navbarNav">
      <ul class="navbar-nav">
        <li class="nav-item">
          <h5>

            <a class="nav-link" style="color: aliceblue;" aria-current="page" href="#">Home</a>
          </h5>
        </li>
        <li class="nav-item">
          <h5>

            <a class="nav-link" style="color: aliceblue;" href="#">Patient</a>
          </h5>
        </li>
        <li class="nav-item">
          <h5>
            <a class="nav-link" style="color: aliceblue;" href="#">Doctor</a>
          </h5>
        </li>

      </ul>
    </div>
  </div>

  <div class="d-flex">
    <button  (click)="addBtn()" type="button"  data-bs-toggle="modal" data-bs-target="#exampleModal" class="btn btn-success">Add Patient</button>
  </div>
</nav>



<table class="table mt-5">
  <thead>
    <tr>

      <th scope="col">First Name</th>
      <th scope="col">Last Name</th>
      <th scope="col">Age</th>
      <th scope="col">Gender</th>
      <th scope="col">Date Of Birth</th>
      <th scope="col">Phone</th>
      <th scope="col">Action</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let n of patientData">

      <td>{{n.firstName}}</td>
      <td>{{n.lastName}}</td>
      <td>{{n.age}}</td>
      <td>{{n.gender}}</td>
      <td>{{n.dob}}</td>
      <td>{{n.phone}}</td>
      <td>
        <button (click)="update(n)" type="button"  data-bs-toggle="modal" data-bs-target="#exampleModal" class="btn btn-info">Edit</button>
        <button (click)="delete(n)" class="btn btn-danger mx-3">Delete</button>
      </td>
    </tr>
  </tbody>
</table>




<!-- Modal -->
<div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h1 class="modal-title fs-5" id="exampleModalLabel">Patient Info</h1>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <form [formGroup] = "formValue">
          <div class="mb-3">
            <label for="exampleInputEmail1" class="form-label">FirstName</label>
            <input formControlName="firstName" type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp">

          </div>

          <div class="mb-3">
            <label  class="form-label">LastName</label>
            <input formControlName="lastName" type="text" class="form-control" id="exampleInputPassword1">
          </div>

          <div class="mb-3">
            <label for="exampleInputPassword1" class="form-label">Age</label>
            <input formControlName="age" type="text" class="form-control" id="exampleInputPassword1">
          </div>

          <div class="mb-3">
            <label class="col-sm-4 form-label">Gender</label>
            <div class="col-sm-8">
                <input formControlName="gender" type="radio" name="Gender" id="GenderMale" value="male" checked> Male
                <input formControlName="gender" type="radio" name="Gender" id="GenderFemale" value="female"> Female
                <input formControlName="gender" type="radio" name="Gender" id="GenderOther" value="others"> Others
            </div>
          </div>

          <div class="mb-3">
            <label for="exampleInputPassword1" class="form-label">Date Of Birth</label>
            <input formControlName="dob" type="text" placeholder="dd/mm/yyyy" class="form-control" id="exampleInputPassword1">
          </div>
          <div class="mb-3">
            <label for="exampleInputPassword1" class="form-label">Phone</label>
            <input formControlName="phone" type="text" class="form-control" id="exampleInputPassword1">
          </div>





      </form>
      </div>
      <div class="modal-footer">
        <button type="button" id="cancle" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" *ngIf="showAdd" (click)="postPatient()" class="btn btn-primary">Add</button>
        <button type="button" *ngIf="showUpdate" (click)="updateBtn()" class="btn btn-primary">update</button>
      </div>
    </div>
  </div>
</div>
