import { Component, Input } from '@angular/core';
import { ApiService } from '../shared/api.service';
import { patientModel } from '../patient/patient.model';
import { FormBuilder,FormGroup } from '@angular/forms';
import { Location } from '@angular/common';
import { ActivatedRoute } from '@angular/router';
// import { PatientComponent } from '../patient/patient.component';
@Component({
  selector: 'app-update',
  templateUrl: './update.component.html',
  styleUrls: ['./update.component.css']
})
export class UpdateComponent {
  formValue !: FormGroup
  patientModelobj !: patientModel;
  patientData !: any;
  currentPatID!: number

  patientId:string|any='';


  constructor(private formbuilder:FormBuilder,private api:ApiService,private location:Location, private activatedRoute : ActivatedRoute) {  }

  ngOnInit(): void {


    this.activatedRoute.params.subscribe(data => {
      this.currentPatID = data['id']      
    })




    this.formValue = this.formbuilder.group({
      firstName :[''],
      lastName :[''],
      age:[''],
      gender:[''],
      dob:[''],
      phone:['']
    })
  }

  getbyId(id:any){
    this.api.getById(thi).subscribe(res => {
      this.patientData = res
    });
  }

  goBack():void{
    this.location.back();
  }


  updateBtn(){
    this.patientModelobj = {
      patientId : this.patientId,
      firstName : this.formValue.value.firstName,
      lastName: this.formValue.value.lastName,
      age: this.formValue.value.age,
      gender: this.formValue.value.gender,
      dob: this.formValue.value.dob,
      phone: this.formValue.value.phone
    };

    console.log(this.patientModelobj);
    this.api.updatePatient(this.patientModelobj,this.patientModelobj.patientId).subscribe(res=>{
      alert("updated");


    })
  }


}
function ngOnInit() {
  throw new Error('Function not implemented.');
}


<div class="total" >

    <form [formGroup]="formValue">

        <div class="mb-3">
            <label  class="form-label">FirstName</label>
            <input formControlName="lastName" value=patientData.firstName type="text" placeholder="FirstName" class="form-control" id="exampleInputPassword1">

        </div>

        <div class="mb-3">
            <label  class="form-label">LastName</label>
            <input formControlName="lastName" value=patient.lastName type="text" placeholder="LastName" class="form-control" id="exampleInputPassword1">
        </div>
        <div class="mb-3">
            <label  class="form-label">Age</label>
            <input formControlName="age"  type="text" placeholder="age" class="form-control" id="exampleInputPassword1">
        </div>
        <div class="mb-3">
            <label  class="form-label">Dob</label>
            <input formControlName="dob" type="text" placeholder="DD/MM/YYYY" class="form-control" id="exampleInputPassword1">
        </div>

        <div class="mb-3">
            <label class="col-sm-4 form-label">Gender</label>
            <div class="col-sm-8">
                <input formControlName="gender" type="radio" name="gender" id="GenderMale" value="male" checked> Male
                <input formControlName="gender" type="radio" name="gender" id="GenderFemale" value="female"> Female
                <input formControlName="gender" type="radio" name="gender" id="GenderOther" value="others"> Others
            </div>
          </div>



        <div class="mb-3">
            <label  class="form-label">Phone</label>
            <input formControlName="phone" type="text" placeholder="Phone" class="form-control" id="exampleInputPassword1">
        </div>

        <button type="submit" (click)="updateBtn()" class="btn btn-primary">Update</button>
        <button type="button" id="cancle" (click)="goBack()" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
      </form> 
</div>
