import { Injectable } from '@angular/core';
import {HttpClient, } from '@angular/common/http'
i
import { map } from 'rxjs/operators';
@Injectable({
  providedIn: 'root'
})
export class ApiService {

  constructor(private http: HttpClient) { }

  postPatient(data:any){
    return this.http.post<any>("https://localhost:3000/posts/",data).pipe(map((res:any)=>{
      return res;
    }))
  }

  getPatient(){
    return this.http.get("http://localhost:3000/posts/").pipe(map((res:any)=>{
      return res;
    }))
  }
  updatePatient(data:any,id:number){
    return this.http.put("https://localhost:3000/posts/"+id,data).pipe(map((res:any)=>{
      return res;
    }))
  }
  deletePatient(id:number){
    return this.http.delete("https://localhost:3000/posts/"+id).pipe(map((res:any)=>{
      return res;
    }))
  }

}
