import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { patientModel } from '../patient/patient.model';
import { Observable } from 'rxjs/internal/Observable';
import { ApiService } from '../shared/api.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-delete',
  templateUrl: './delete.component.html',
  styleUrls: ['./delete.component.css']
})
export class DeleteComponent {

  patientModelobj !: patientModel;
  patientData !: Observable<any>;
  currentPatID!: number
  patientId:string|any='';


  constructor(private api:ApiService,private route:ActivatedRoute){}

  ngOnInit():void{
    this.route.params.subscribe(data => {
      this.currentPatID = data['id']
      console.log(this.currentPatID);


      this.patientData = this.api.getById(this.currentPatID);
    })




    // console.log(this.getbyId(13));




  }
  getAll(){
    this.api.getPatient().subscribe(res=>{
      this.patientData = res;
      })
  }
  getbyId(id:any){
    this.api.getById(id).subscribe(res => {
      this.patientData = res
      console.log(res);
    });
  }




}
