import { Component, OnInit } from '@angular/core';
import { FormBuilder,FormGroup } from '@angular/forms';
import { patientModel } from './patient.model';
import { ApiService } from '../shared/api.service';
import { Route, Router } from '@angular/router';
import { Location } from '@angular/common';
import { ActivatedRoute } from '@angular/router';
// import { constructor } from 'typescript';
// import { DeleteComponent } from '../delete/delete.component';
@Component({
  selector: 'app-patient',
  templateUrl: './patient.component.html',
  styleUrls: ['./patient.component.css'],
  providers:[ApiService]
})


export class PatientComponent implements OnInit{
  formValue !: FormGroup;
  patientModelobj !: patientModel;
  patientData !: patientModel[];
  showAdd !:boolean;
  currentPatID!: number;
  data !: []
  showDeletePopUP:boolean = false;

  showUpdate !:boolean;




  constructor(private formbuilder: FormBuilder,
    private api:ApiService,
    private act:ActivatedRoute,
    private route:Router,
    private location:Location,

    ) {
  }


  gotoUpdate(id: number):void{
    //this.route.navigate([`${pageName}`])
    this.route.navigate(['update', id]);
  }

  goback(){
    this.location.back();
  }


  gotoDelete(id:any):void{

    this.showDeletePopUP = true
  }
  ngOnInit(){





    this.formValue = this.formbuilder.group({
      firstName :[''],
      lastName :[''],
      age:[''],
      gender:[''],
      dob:[''],
      phone:['']

    })
    this.getAll();



  }

  addBtn(){
    this.formValue.reset();
    this.showAdd = true;
    this.showUpdate = false;
  }


  postPatient(){

    this.patientModelobj = {
      patientId : this.currentPatID,
      firstName : this.formValue.value.firstName,
      lastName: this.formValue.value.lastName,
      age: this.formValue.value.age,
      gender: this.formValue.value.gender,
      dob: this.formValue.value.dob,
      phone: this.formValue.value.phone
    };

    this.api.postPatient(this.patientModelobj).subscribe(res=>{
      console.log(res);
      alert("Patient added successfully")
      this.formValue.reset();
      this.getAll();
      let ref = document.getElementById('cancle');
      ref?.click();
    },
    err => {
      alert("Something went wrong")
    })
  }
  getAll(){
    this.api.getPatient().subscribe(res=>{
      this.patientData = res;
    })
  }
  delete(id:number){
    this.api.deletePatient(id).subscribe(res=>{
      alert("Deleted")
      this.getAll();
    })
  }
  update(n:any){


    this.currentPatID = n.patientId;    
    this.showUpdate = true;
    this.showAdd = false;
    //this.patientModelobj.patientId = n.patientId;
    this.formValue.controls['firstName'].setValue(n.firstName);
    this.formValue.controls['lastName'].setValue(n.lastName);
    this.formValue.controls['age'].setValue(n.age);
    this.formValue.controls['gender'].setValue(n.gender);
    this.formValue.controls['dob'].setValue(n.dob);
    this.formValue.controls['phone'].setValue(n.phone);



  }

  updateBtn(){
    this.patientModelobj = {
      patientId : this.currentPatID,
      firstName : this.formValue.value.firstName,
      lastName: this.formValue.value.lastName,
      age: this.formValue.value.age,
      gender: this.formValue.value.gender,
      dob: this.formValue.value.dob,
      phone: this.formValue.value.phone
    };

    console.log(this.patientModelobj);
    this.api.updatePatient(this.patientModelobj,this.patientModelobj.patientId).subscribe(res=>{
      alert("updated");
      let ref = document.getElementById('cancle');
      ref?.click();
      this.getAll();
    })
  }








}
<div class="Whole">

  <div class="d-flex">
    <button style="position: relative; margin-left: 90%;margin-top: 1%;" (click)="addBtn()" type="button"  data-bs-toggle="modal" data-bs-target="#exampleModal" class="btn btn-success">Add Patient</button>
  </div>



<table class="table ">
<thead>
  <tr>

    <th scope="col">First Name</th>
    <th scope="col">Last Name</th>
    <th scope="col">Age</th>
    <th scope="col">Gender</th>
    <th scope="col">Date Of Birth</th>
    <th scope="col">Phone</th>
    <th scope="col">Action</th>
  </tr>
</thead>
<tbody *ngFor="let n of patientData">
  <tr  >
    <td appApphighlet [age] = [n.age]>{{n.firstName}}</td>
    <td appApphighlet [age] = [n.age]>{{n.lastName}}</td>
    <td appApphighlet [age] = [n.age]>{{n.age}}</td>
    <td appApphighlet [age] = [n.age]>{{n.gender}}</td>
    <td appApphighlet [age] = [n.age]>{{n.dob}}</td>
    <td appApphighlet [age] = [n.age]>{{n.phone}}</td>

    <td>
      <button (click)="update(n)"  type="button"  data-bs-toggle="modal" data-bs-target="#exampleModal" class="btn btn-info">Edit</button>

      <button href="Delete/"  (click)="gotoDelete(n.patientId)" style="margin-left: 2%;margin-right: 2%;" class="btn btn-danger mx-3" type="button"  data-bs-toggle="modal" data-bs-target="#staticBackdrop">Delete</button>
      <button href="Update/" (click)="gotoUpdate(n.patientId)" class="btn btn-dark">Edit-2</button>
    </td>
  </tr>
</tbody>
</table>

<app-delete  *ngIf="this.showDeletePopUP == true"></app-delete>



<!-- Modal -->
<div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
<div class="modal-dialog">
  <div class="modal-content">
    <div class="modal-header">
      <h1 class="modal-title fs-5" id="exampleModalLabel">Patient Info</h1>
      <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
    </div>
    <div class="modal-body">
      <form [formGroup] = "formValue">
        <div class="mb-3">
          <label for="exampleInputEmail1" class="form-label">FirstName</label>
          <input formControlName="firstName" type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp">

        </div>

        <div class="mb-3">
          <label  class="form-label">LastName</label>
          <input formControlName="lastName" type="text" class="form-control" id="exampleInputPassword1">
