import { Directive, ElementRef, OnInit,Input ,HostListener} from '@angular/core';

@Directive({
  selector: '[appApphighlet]'
})
export class ApphighletDirective implements OnInit{

  @Input() 
  constructor(private ele:ElementRef) { }


    @HostListener('mouseenter') onMouseEnter() {
      this.ele.nativeElement.style.backgroundColor = this.customParameter;
    }

    @HostListener('mouseleave') onMouseLeave() {
      this.ele.nativeElement.style.backgroundColor = null;
    }
    // this.ele.nativeElement.style.backgroundColor = 'red'


  ngOnInit(): void {

  }

}
